mapper.insert:

insert into db_config (SID, 'NAME', OPER, 
  RESERVE3, RESERVE4, OPERTIME, 
  RESERVE1, RESERVE2, 'DESC'
  )
values (#{sid,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{oper,jdbcType=VARCHAR}, 
  #{reserve3,jdbcType=DECIMAL}, #{reserve4,jdbcType=DECIMAL}, #{opertime,jdbcType=TIMESTAMP}, 
  #{reserve1,jdbcType=LONGVARCHAR}, #{reserve2,jdbcType=LONGVARCHAR}, #{desc,jdbcType=LONGVARCHAR}
  )

mapper.select:

select SID, 'NAME', OPER, RESERVE3, RESERVE4, OPERTIME, RESERVE1, RESERVE2, 'DESC'
from db_config
where SID = #{sid,jdbcType=VARCHAR}
