mapper.select:
select 

from stat_hotproduct
where YYYYMMDD = #{yyyymmdd,jdbcType=VARCHAR}
  and SHOPTYPEID = #{shoptypeid,jdbcType=VARCHAR}
  and PRODUCTID = #{productid,jdbcType=VARCHAR}

mapper.delete:
delete from stat_hotproduct
where YYYYMMDD = #{yyyymmdd,jdbcType=VARCHAR}
  and SHOPTYPEID = #{shoptypeid,jdbcType=VARCHAR}
  and PRODUCTID = #{productid,jdbcType=VARCHAR}
