mapper.select:
select YYYYMMDD, SHOPTYPEID, PRODUCTID, SHOPTYPENAME, PRODUCTNAME, ORDERNUM, CATEGORYID, 
CATEGORYNAME, SELLOPER, PURCHASEOPER, RESERVE1, RESERVE2, RESERVE3, RESERVE4
from STAT_HOTPRODUCT
where YYYYMMDD = #{yyyymmdd,jdbcType=VARCHAR}
  and SHOPTYPEID = #{shoptypeid,jdbcType=VARCHAR}
  and PRODUCTID = #{productid,jdbcType=VARCHAR}
